            parseBtnClass: function (btnCss) {

                // 处理EDFA01中注册的按钮的样式
                btnCss = btnCss || "";
                var resources = btnCss.trim().split(":"); // 目前只支持 css:
                var btnClass;

                if (resources.length > 1) {
                    btnClass = resources[1];
                } else {
                    btnClass = resources[0];
                }

                // css:
                // fa fa-lock
                // k-icon k-lock
                // i-icon i-lock

                if (btnClass.match(/fa-/)) {
                    btnClass = "i-btn-gap " + btnClass;
                    if (!btnClass.match(/^fa /)) { // 补全font awesome的class
                        btnClass = "fa " + btnClass;
                    }
                } else if (btnClass.match(/k-/)) {
                    if (!btnClass.match(/^k-icon /)) { // 补全kendo icon 的class
                        btnClass = "k-icon " + btnClass;
                    }
                } else if (btnClass.match(/i-/)) {
                    if (!btnClass.match(/^i-icon /)) { // 补全iplat icon 的class
                        btnClass = "i-icon " + btnClass;
                    }
                }

                return btnClass;
            },

