        a.push(',', EiInfoJsonConstants.BLOCK_META, ':', EiInfo2Json.prototype.EiBlockMeta2JsonString(eiBlock.getBlockMeta()));

        a.push(',', EiInfoJsonConstants.BLOCK_DATA, ':[');

        var rows = eiBlock.getRows();
