        for (var i = 0; i < rows.length; i++) {
            var row = rows[i];
            if (b) {
                a.push(',');
            }
            a.push('[');
            var columns = eiBlock.getBlockMeta().getMetas();
            var c;
            for (var jKey in columns) {
                var column = columns[jKey];
                if (c) {
                    a.push(',');
                }
                a.push(formatNative(row[column.pos]));
                c = true;
            }
            b = true;
            c = false;
            a.push(']');
        }

        a.push(']}');
